require( 'workflows.php' );
$w = new Workflows();
$query = "{query}";
$data = file_get_contents(__DIR__.'/category.json');
$data = json_decode( $data );
$pattern='/^(\d+) (➣) (.+)?$/';
preg_match($pattern,$query,$match);
if ($query == '') {
	$w->result( 0, 0, 'All', '', 'icon.png', 'no', '0 ➣ ' );
	foreach ($data as $item) {
		$w->result( $item->id, $item->id, $item->name, 'in '. $item->in, 'icon.png', 'no', $item->id .' ➣ ' );
	};
}
elseif($match[0] == $query){
	$list = $w->request( "http://apify.ifc0nfig.com/tpb/top?id=".urlencode( $match[1] ) );
	$list = json_decode( $list );
	if($match[3] == ''){
		foreach ($list as $item) {
			$w->result( $item->id, $item->magnet, $item->name, $item->size .', '. $item->seeders .' seeders, '. $item->leechers .' leechers, in '. $item->category, 'icon.png' );	
		};
	}
	else {
	 	foreach ($list as $item) {
	 	  	$pos = $pos = strpos(strtolower($item->name), strtolower($match[3]));
	 	  	if($pos !== false) {
	 	  	 	$w->result( $item->id, $item->magnet, $item->name, $item->size .', '. $item->seeders .' seeders, '. $item->leechers .' leechers, in '. $item->category, 'icon.png' );	
	 	  	}	
	 	};	
	}	
}
else{
foreach ($data as $item) {
	$pos = strpos(strtolower($item->name), strtolower($query));
	if($pos !== false) {
		$w->result( $item->id, $item->id, $item->name, 'in '. $item->in, 'icon.png', 'no', $item->id .' ➣ ' );
	}
};
}
echo $w->toxml();

require( 'workflows.php' );
$w = new Workflows();
$query = "{query}";
$data = file_get_contents(__DIR__.'/category.json');
$data = json_decode( $data );
$pattern='/^(.+) (➣) (.+)$/';
preg_match($pattern,$query,$match);
if ($query == '') {
	foreach ($data as $item) {
		if($item->in !== 'All') {
			$w->result( $item->id, $item->id, $item->name, 'in '. $item->in, 'icon.png', 'no', $item->name .' ➣ ' );
		}
	};
}
elseif($match[0] == $query){
	$url = "http://apify.ifc0nfig.com/tpb/search/". urlencode( $match[3] ) ."?\$filter=category%20eq%20'". urlencode( $match[1] ) ."'&top=10";
	$list = $w->request( $url );
	$list = json_decode( $list );

	foreach ($list as $item) {
	  $pos = $pos = strpos(strtolower($item->name), strtolower($match[3]));
	  if($pos !== false) {
		$w->result( $item->id, $item->magnet, $item->name, $item->size .', '. $item->seeders .' seeders, '. $item->leechers .' leechers, in '. $item->category, 'icon.png' );			
		}
	};
}
else{
foreach ($data as $item) {
	if($item->in !== 'All') {
		$pos = strpos(strtolower($item->name), strtolower($query));
		if($pos !== false) {
			$w->result( $item->id, $item->id, $item->name, 'in '. $item->in, 'icon.png', 'no', $item->name .' ➣ ' );
		}
	}

};
}
echo $w->toxml();

